import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { addSubtask, toggleSubtask, deleteSubtask, editSubtask } from "./tasksSlice";

const TaskModal = ({ task, isOpen, onClose }) => {
  const dispatch = useDispatch();
  const [text, setText] = useState("");

  useEffect(() => {
    if (isOpen) setText("");
  }, [isOpen, task]);

  // keep the task fresh from store so updates reflect immediately in the modal
  const latestTask = useSelector((s) =>
    task ? s.tasks.items.find((t) => t._id === task._id) : null
  ) || task;

  const [editingId, setEditingId] = useState(null);
  const [editText, setEditText] = useState("");

  if (!isOpen || !latestTask) return null;

  const handleAdd = async (e) => {
    e.preventDefault();
    if (!text.trim()) return;
    try {
      await dispatch(addSubtask({ id: latestTask._id, text })).unwrap();
      setText("");
    } catch (err) {
      alert(err || "Can't add subtask!");
    }
  };

  const handleToggle = async (sub) => {
    try {
      await dispatch(toggleSubtask({ id: latestTask._id, subId: sub.id })).unwrap();
    } catch (err) {
      alert(err || "Failed to toggle subtask");
    }
  };

  const handleDelete = async (sub) => {
    if (!confirm("Delete this subtask?")) return;
    try {
      await dispatch(deleteSubtask({ id: latestTask._id, subId: sub.id })).unwrap();
    } catch (err) {
      alert(err || "Failed to delete subtask");
    }
  };

  const handleEditStart = (sub) => {
    setEditingId(sub.id);
    setEditText(sub.text);
  };

  const handleEditCancel = () => {
    setEditingId(null);
    setEditText("");
  };

  const handleEditSave = async (sub) => {
    if (!editText.trim()) return;
    try {
      await dispatch(editSubtask({ id: latestTask._id, subId: sub.id, text: editText })).unwrap();
      setEditingId(null);
      setEditText("");
    } catch (err) {
      alert(err || "Failed to edit subtask");
    }
  };

  return (
    <div className="modal-backdrop" onClick={onClose}>
      <div className="modal-card" onClick={(e) => e.stopPropagation()}>
        <div style={{ padding: 20, background: latestTask.color || "#3b82f6", color: "#fff" }}>
          <div style={{ display: "flex", justifyContent: "space-between", alignItems: "start" }}>
            <div className="modal-header">
              <h3 className="modal-title" style={{ margin: 8 }}>
                {latestTask.title}
              </h3>
              <div className="modal-desc" style={{ opacity: 0.9 }}>
                {latestTask.description}{" "}
              </div>
            </div>
            <div>
              <button className="btn ghost" onClick={onClose}>
                Close
              </button>
            </div>
          </div>
        </div>

        <div className="modal-subtasks-list">
          {(!latestTask.subtasks || latestTask.subtasks.length === 0) && (
            <div style={{ textAlign: "center", padding: 28, color: "#9aa4b2" }}>
              No subtasks yet.
            </div>
          )}

          {latestTask.subtasks?.map((sub) => (
            <div className="subtask-item" key={sub.id}>
              <input type="checkbox" checked={!!sub.completed} onChange={() => handleToggle(sub)} />
              {editingId === sub.id ? (
                <div style={{ display: "flex", gap: 8, alignItems: "center", flex: 1 }}>
                  <input
                    value={editText}
                    onChange={(e) => setEditText(e.target.value)}
                    style={{ flex: 1 }}
                  />
                  <button className="btn ghost" onClick={() => handleEditCancel()}>
                    Cancel
                  </button>
                  <button className="btn" onClick={() => handleEditSave(sub)}>
                    Save
                  </button>
                </div>
              ) : (
                <>
                  <div
                    className={`subtask-text ${sub.completed ? "task-completed" : ""}`}
                    style={{
                      textDecoration: sub.completed ? "line-through" : "none",
                      backgroundColor: sub.completed ? "#94f5c7ff" : "#fff",
                    }}
                  >
                    {sub.text}
                  </div>
                  <div style={{ marginLeft: "auto", display: "flex", gap: 8 }}>
                    <button className="btn ghost" onClick={() => handleEditStart(sub)}>
                      Edit
                    </button>
                    <button className="btn ghost" onClick={() => handleDelete(sub)}>
                      Delete
                    </button>
                  </div>
                </>
              )}
            </div>
          ))}
        </div>

        <div className="modal-addSubtask">
          <form className="modal-form" onSubmit={handleAdd}>
            <input
              value={text}
              onChange={(e) => setText(e.target.value)}
              placeholder="Add subtask..."
            />
            <button className="btn" type="submit">
              Add
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default TaskModal;
