import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { updateProfile } from "./authSlice";

const UserModal = ({ isOpen, onClose }) => {
  const dispatch = useDispatch();
  const user = useSelector((s) => s.auth.user);
  const [editing, setEditing] = useState(false);
  const [username, setUsername] = useState(user?.username || "");
  const [password, setPassword] = useState("");
  const [profileImage, setProfileImage] = useState(null);
  const [saving, setSaving] = useState(false);

  if (!isOpen || !user) return null;

  const handleStartEdit = () => {
    setEditing(true);
    setUsername(user.username || "");
  };

  const handleCancel = () => {
    setEditing(false);
    setPassword("");
    setProfileImage(null);
  };

  const handleSave = async () => {
    setSaving(true);
    try {
      const formData = new FormData();
      if (username && username !== user.username) formData.append("username", username);
      if (password) formData.append("password", password);
      if (profileImage) formData.append("profileImage", profileImage);

      let hasChanges = false;
      // eslint-disable-next-line no-unused-vars
      for (let _ of formData.entries()) {
        hasChanges = true;
        break;
      }
      if (!hasChanges) {
        setSaving(false);
        setEditing(false);
        return;
      }
      await dispatch(updateProfile(formData)).unwrap();
      setPassword("");
      setProfileImage(null);
      setEditing(false);
    } catch (err) {
      alert(err || "Failed to update profile");
    } finally {
      setSaving(false);
    }
  };

  const profileImageUrl = user.profileImage ? `http://localhost:5000${user.profileImage}` : "/no-image.png";

  return (
    <div className="modal-backdrop" onClick={onClose}>
      <div className="modal-card" onClick={(e) => e.stopPropagation()}>
        <div style={{ padding: 20, background: "#f9fafb" }}>
          <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center" }}>
            <div style={{ display: "flex", alignItems: "center", gap: 16 }}>
              <img
                src={profileImageUrl}
                alt="profile"
                style={{ width: 80, height: 80, borderRadius: "50%", objectFit: "cover" }}
              />
              <div>
                <h3 style={{ margin: 0 }}>{user.username}</h3>
                <div className="small">{user.email}</div>
              </div>
            </div>
            <div>
              <button className="btn ghost" onClick={onClose}>
                Close
              </button>
            </div>
          </div>
        </div>

        <div style={{ padding: 16 }}>
          {!editing ? (
            <div>
              <p className="small">Click Settings to change username, password, or profile picture.</p>
              <div style={{ marginTop: 8 }}>
                <button className="btn ghost" onClick={handleStartEdit}>
                  Settings
                </button>
              </div>
            </div>
          ) : (
            <div style={{ display: "flex", flexDirection: "column", gap: 12 }}>
              <div>
                <label>Username</label>
                <input value={username} onChange={(e) => setUsername(e.target.value)} />
              </div>
              <div>
                <label>New Password</label>
                <input
                  type="password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                />
              </div>
              <div>
                <label>Profile Picture</label>
                <input type="file" accept="image/*" onChange={(e) => setProfileImage(e.target.files?.[0] || null)} />
              </div>
              <div style={{ display: "flex", gap: 8 }}>
                <button className="btn" onClick={handleSave} disabled={saving}>
                  {saving ? "Saving..." : "Save"}
                </button>
                <button className="btn ghost" onClick={handleCancel}>
                  Cancel
                </button>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default UserModal;
