import React, { useEffect, useState } from "react";
import { useDispatch } from "react-redux";
import { addSubtask, toggleSubtask, deleteSubtask } from "./tasksSlice";

const TaskModal = ({ task, isOpen, onClose }) => {
  const dispatch = useDispatch();
  const [text, setText] = useState("");

  useEffect(() => {
    if (isOpen) setText("");
  }, [isOpen, task]);

  if (!isOpen || !task) return null;

  const handleAdd = async (e) => {
    e.preventDefault();
    if (!text.trim()) return;
    try {
      await dispatch(addSubtask({ id: task._id, text })).unwrap();
      setText("");
    } catch (err) {
      alert("Can't add subtask!");
    }
  };

  const handleToggle = (sub) => {
    dispatch(toggleSubtask({ id: task._id, subId: sub.id }));
  };

  const handleDelete = (sub) => {
    // if (!confirm('Delete this subtask?')) return;
    dispatch(deleteSubtask({ id: task._id, subId: sub.id }));
  };

  return (
    <div className="modal-backdrop" onClick={onClose}>
      <div className="modal-card" onClick={(e) => e.stopPropagation()}>
        <div style={{ padding: 20, background: task.color || "#3b82f6", color: "#fff" }}>
          <div style={{ display: "flex", justifyContent: "space-between", alignItems: "start" }}>
            <div className="modal-header">
              <h3 className="modal-title" style={{ margin: 8 }}>
                {task.title}
              </h3>
              <div className="modal-desc" style={{ opacity: 0.9 }}>
                {task.description}{" "}
              </div>
            </div>
            <div>
              <button className="btn ghost" onClick={onClose}>
                Close
              </button>
            </div>
          </div>
        </div>

        <div className="modal-subtasks-list">
          {(!task.subtasks || task.subtasks.length === 0) && (
            <div style={{ textAlign: "center", padding: 28, color: "#9aa4b2" }}>
              No subtasks yet.
            </div>
          )}

          {task.subtasks?.map((sub) => (
            <div className="subtask-item" key={sub.id}>
              <input type="checkbox" checked={!!sub.completed} onChange={() => handleToggle(sub)} />
              <div
                className={`subtask-text ${sub.completed ? "task-completed" : ""}`}
                style={{
                  textDecoration: sub.completed ? "line-through" : "none",
                  backgroundColor: sub.completed ? "#94f5c7ff" : "#fff",
                }}
              >
                {sub.text}
              </div>
              <div style={{ marginLeft: "auto" }}>
                <button className="btn ghost" onClick={handleDelete}>
                  Delete
                </button>
              </div>
            </div>
          ))}
        </div>

        <div className="modal-addSubtask">
          <form className="modal-form" onSubmit={handleAdd}>
            <input
              value={text}
              onChange={(e) => setText(e.target.value)}
              placeholder="Add subtask..."
            />
            <button className="btn" type="submit">
              Add
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default TaskModal;
